import { css, html, LitElement } from "@polymer/lit-element";

import "@material/mwc-button";


class AbstractActionPage extends LitElement {
  render() {
    return html`
      <div id="layout">
        <slot></slot>
      </div>
      <div id="abstractActions">
        <mwc-button @click="${this.cancel}" raised>Cancel</mwc-button>
        <mwc-button @click="${this.accept}" raised class="accent">Accept</mwc-button>
      </div>
    `;
  }

  accept() {
    let event = new CustomEvent('accept', { });
    this.dispatchEvent(event);
  }

  cancel() {
    let event = new CustomEvent('cancel', { });
    this.dispatchEvent(event);
  }

  static get styles() {
    return [
      css`
        :host {
          padding: 22px;
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          background-color: var(--primary-background-color);
        }
        #layout {
          display: flex;
          flex-direction: row;
          align-items: center;
          justify-content: center;
        }
        #abstractActions {
          padding-top: 30px;
          flex-shrink: 0;
        }
        mwc-button.accent {
          color: var(--paper-button-accent);
        }




        /* customized mwc-input styles so the label appears to the left */
        :host::shadow mwc-input {
          position: relative;
          width: 240px;
        }
        :host::shadow mwc-input::shadow mwc-input-container {
          padding: 3px 0;
        }
        :host::shadow mwc-input::shadow mwc-input-container.mwc-input div.input-content.label-is-floating.mwc-input-container label {
          position: absolute;
          left: inherit !important;
          right: 260px;
          top: 23px;
          white-space: nowrap;
          transform-origin: right top;
        }
      `,
    ];
  }
}

window.customElements.define('abstract-action-page', AbstractActionPage);
