import { css, html, LitElement } from "@polymer/lit-element";

import "./song-list-item.js";
import "./star-rating.js";


class MixListItem extends LitElement {
  render() {
    let rating = Math.round(this.mix.rank / 20);
    return html`
      <song-list-item .song="${this.mix.outSong}"></song-list-item>
      <div class="info">
        <star-rating value="${rating}" readonly></star-rating>
        <div>${this.mix.comments}</div>
      </div>
    `;
  }

  static get properties() {
    return {
      mix: { type: Object },
    };
  }

  constructor() {
    super();
  }

  static get styles() {
    return [
      css`
        :host {
          display: flex;
          flex-direction: column;
        }
        star-rating {
          margin: 0 6px;
        }
        .info {
          display: flex;
          flex-direction: row;
          align-items: center;
          height: 30px;
          padding: 0 8px;
          margin: 0 10px 0 60px;
          border-bottom: var(--ss-song-list-item-border-bottom);
        }
      `,
    ];
  }
}

window.customElements.define('mix-list-item', MixListItem);
